// this is a hack to allow us to commit bins to git

var modulesToTry = ['mmmagic_production', 'mmmagic_mac', 'mmmagic_toks', 'mmmagic_ubuntu'];

for (var i = 0; i < modulesToTry.length; i++) {
	try {
		module.exports = require(modulesToTry[i]);
		if (process.env.DEBUG_LEVEL === 'verbose') {
			console.log('loaded ' + modulesToTry[i]);
		}
		return;
	} catch (e) {
		if (process.env.DEBUG_LEVEL === 'verbose') {
			console.log('tried loading ' + modulesToTry[i] + ' but failed');
		}
	}
}

throw new Error("Could not load the mmmagic module");
